const path = require('path');
const binaryHash = require('./binary-hash');

module.exports = [
  {
    name: 'binary-hash',
    description: 'Calculate hash for native binary',
    func: binaryHash,
    options: [
      {
        name: '--platform <string>',
        description: 'Either "ios" or "android"',
        default: 'ios',
      },
      {
        name: '--folders <list>',
        description:
          'Specify any additional folders to be included in the hash',
        parse: (val) => val.split(',').map((folder) => path.resolve(folder)),
      },
    ],
  },
];
